def solution(stdin: str) -> str:
    # Read input
    N, M = map(int, stdin.split()[:2])
    edges = [tuple(map(int, stdin.split()[2*i:2*i+2])) for i in range(M)]

    # Find the adjacency list
    adjacency_list = [[] for _ in range(N)]
    for i in range(M):
        a, b = edges[i]
        adjacency_list[a-1].append(b-1)
        adjacency_list[b-1].append(a-1)

    # Find the number of bridges
    bridges = 0
    for i in range(N):
        visited = [False] * N
        dfs = [False] * N
        stack = []
        dfs[i] = True
        stack.append(i)
        while stack:
            v = stack[-1]
            if dfs[v]:
                visited[v] = True
                stack.pop()
                for w in adjacency_list[v]:
                    if not visited[w]:
                        dfs[w] = True
                        stack.append(w)
            else:
                for w in adjacency_list[v]:
                    if visited[w] and not dfs[w]:
                        bridges += 1
                dfs[v] = True

    return str(bridges)

