def solution(stdin: str) -> str:
    game_state, move = stdin.strip().split('\n')
    game_state = [[int(x) for x in line.split()] for line in game_state.split('\n')]
    move = int(move)

    def can_move(direction):
        result = [[0 for _ in range(4)] for _ in range(4)]
        for i in range(4):
            for j in range(4):
                if game_state[i][j] == 0:
                    continue
                new_i, new_j = i, j
                while True:
                    if direction == 0:
                        new_i -= 1
                    elif direction == 1:
                        new_j -= 1
                    elif direction == 2:
                        new_i += 1
                    elif direction == 3:
                        new_j += 1
                    if not (0 <= new_i < 4 and 0 <= new_j < 4):
                        break
                    if game_state[new_i][new_j] != 0:
                        if direction == 0:
                            result[i][j] = game_state[new_i][new_j]
                        elif direction == 1:
                            result[j][i] = game_state[new_j][new_i]
                        elif direction == 2:
                            result[i][j] = game_state[new_i][new_j]
                        elif direction == 3:
                            result[j][i] = game_state[new_j][new_i]
                        break
                    else:
                        new_i, new_j = new_i, new_j
        return result

    def merge(direction):
        temp_game_state = can_move(direction)
        for i in range(4):
            for j in range(4):
                if game_state[i][j] == 0 and temp_game_state[i][j] != 0:
                    game_state[i][j] = temp_game_state[i][j]

    if move == 0:
        game_state = can_move(0)
    elif move == 1:
        game_state = can_move(1)
    elif move == 2:
        game_state = can_move(2)
    elif move == 3:
        merge(0)
        merge(1)
        merge(2)
        merge(3)
        game_state = can_move(0)
        if not any(any(x != 0 for x in row) for row in game_state):
            game_state = can_move(0)
        if not any(any(x != 0 for x in row) for row in game_state):
            game_state = can_move(1)
        if not any(any(x != 0 for x in row) for row in game_state):
            game_state = can_move(2)
        if not any(any(x != 0 for x in row) for row in game_state):
            game_state = can_move(3)

    game_state = [[str(x) for x in row] for row in game_state]
    return '\n'.join([' '.join(row) for row in game_state])

