def solution(stdin: str) -> str:
    n, *a = map(int, stdin.split())
    a = list(a)
    socks = [True] * (n * 2)
    def aux_move(i: int) -> None:
        nonlocal socks
        if socks[i]:
            socks[i] = False
            socks[i + n] = True
    def sock_move(i: int, j: int) -> None:
        nonlocal socks
        if socks[i] and socks[j]:
            socks[i] = False
            socks[j] = False
    moves = 0
    for i in range(n):
        if a[i] != a[i + n]:
            aux_move(i)
            moves += 1
    for i in range(n, 2 * n - 1):
        if a[i] != a[i + 1]:
            sock_move(i, i + 1)
            moves += 1
    for i in range(n - 1):
        if a[i] != a[i + 2]:
            sock_move(i, i + 2)
            moves += 1
    if all(socks[i] == False for i in range(2 * n)):
        return str(moves)
    else:
        return "impossible"

