def solution(stdin: str) -> str:
    def hamming_distance(str1: str, str2: str) -> int:
        return sum(s1 != s2 for s1, s2 in zip(str1, str2))

    def minimum_unlikeliness(samples: List[str]) -> Tuple[int, List[Tuple[int, int]]]:
        n = len(samples)
        adjacency_matrix = [[0] * n for _ in range(n)]
        min_unlikeliness = float('inf')
        min_tree = []

        for i in range(n):
            for j in range(i + 1, n):
                unlikeliness = hamming_distance(samples[i], samples[j])
                adjacency_matrix[i][j] = unlikeliness
                adjacency_matrix[j][i] = unlikeliness

            current_unlikeliness = sum(adjacency_matrix[i])
            if current_unlikeliness < min_unlikeliness:
                min_unlikeliness = current_unlikeliness
                min_tree = [(i, j) for j in range(n) if i != j]

        return min_unlikeliness, min_tree

    def main(stdin: str) -> str:
        n, k = map(int, stdin.split()[:2])
        samples = [input().strip() for _ in range(n)]

        min_unlikeliness, min_tree = minimum_unlikeliness(samples)

        output = [str(min_unlikeliness)]
        output.extend(f"{u} {v}" for u, v in min_tree)
        return "\n".join(output)

    return main(stdin)

