    # function to check if two words are mutually anagrams
    def is_anagram(word1: str, word2: str) -> bool:
        return sorted(word1) == sorted(word2)

    # function to find the shortest root of the multigram
    def find_shortest_root(word: str) -> str:
        if len(word) == 1:
            return word
        for i in range(1, len(word)):
            if is_anagram(word[:i], word[i:]):
                return find_shortest_root(word[:i])
        return word

    # split the input word into subwords
    words = [word for word in stdin]

    # check if the input word is a multigram
    if len(words) < 2:
        return -1
    for i in range(len(words)-1):
        if not is_anagram(words[i], words[i+1]):
            return -1

    # find the shortest root of the multigram
    return find_shortest_root(stdin)

