def solution(stdin: str) -> str:
    R, C = map(int, stdin.split()[0:2])
    rows = stdin.split('\n')[1:R+1]

    # create a matrix of the crossword puzzle
    puzzle = [list(row) for row in rows]

    # define the function to find the lexicographically smallest word
    def find_word(i, j, direction):
        if direction == 'horizontal':
            k, l = j, j
            while 0 <= k < C and '#' not in puzzle[i][k]:
                k += 1
            while 0 <= l < C and '#' not in puzzle[i][l]:
                l -= 1
            return ''.join(puzzle[i][k+1:l+1])
        else:
            k, l = i, i
            while 0 <= k < R and '#' not in puzzle[k][j]:
                k += 1
            while 0 <= l < R and '#' not in puzzle[l][j]:
                l -= 1
            return ''.join(row[j+1:j-l-1:-1] for row in puzzle[k+1:l+1])

    # find the lexicographically smallest word
    words = []
    for i in range(R):
        for j in range(C):
            if puzzle[i][j] == '#':
                continue
            for direction in ['horizontal', 'vertical']:
                word = find_word(i, j, direction)
                if len(word) >= 2:
                    words.append(word)
    words.sort()
    return words[0]

