    macro_dict = {'N': 'N', 'S': 'S', 'E': 'E', 'W': 'W'}

    def expand_macro(macro_str):
        result = ''
        for char in macro_str:
            result += macro_dict[char]
        return result

    def count_chars(route):
        count = 0
        for char in route:
            count += 1 if char in macro_dict else 2
        return count

    # Check if route contains any macro characters
    if 'M' in route:
        macro_key = route.index('M')
        macro_val = route[macro_key+1:macro_key+3]
        macro['M'] = expand_macro(macro_val)
        route = route[:macro_key] + macro['M'] + route[macro_key+3:]

    return count_chars(route)

