def solution(stdin: str) -> str:
    input_list = stdin.splitlines()
    P, D = map(int, input_list.pop(0).split())
    district_results = {}

    for _ in range(D):
        district, a, b = map(int, input_list.pop(0).split())
        if a > b:
            winner = "A"
        else:
            winner = "B"
        wasted_votes = b - max(a, (a + b) // 2)
        district_results[district] = (winner, wasted_votes)

    total_wasted_votes_a = sum(wasted_votes for _, (_, wasted_votes) in district_results.items() if wasted_votes < 0)
    total_wasted_votes_b = sum(wasted_votes for _, (_, wasted_votes) in district_results.items() if wasted_votes > 0)
    efficiency_gap = abs(total_wasted_votes_a - total_wasted_votes_b) / (P + D)

    output = []
    for district in sorted(district_results.keys()):
        winner, wasted_votes = district_results[district]
        output.append(f"{winner} {abs(wasted_votes)}")

    return "\n".join(output) + "\n" + str(efficiency_gap)

