    M, N = map(int, stdin.split()[0:2])
    coins = [list(map(int, stdin.split()[2:])) for _ in range(M)]

    def min_ladder_length(source: Tuple[int, int], target: Tuple[int, int]) -> int:
        m, n = source
        p, q = target
        height_diff = coins[p][q] - coins[m][n]

        if height_diff < 0:
            return 0

        if height_diff == 0:
            return abs(p - m) + abs(q - n)

        if height_diff % 2 == 0:
            return height_diff // 2 + abs(p - m) + abs(q - n)
        else:
            return height_diff // 2 + 1 + abs(p - m) + abs(q - n)

    return str(min_ladder_length((0, 0), (M - 1, N - 1)))

