    N = float(stdin.strip())

    if N < 0.36788 or N > 2.718281:
        return "Invalid input. N must be between 0.36788 and 2.718281."

    if N >= 1 / math.e and N <= math.e:
        a = math.exp(1)
        while True:
            if math.isclose(a ** a, N, rel_tol=1e-5):
                return str(a)
            a *= math.exp(1)
    else:
        return "No solution exists for N outside the range 1/e to e."

