    grid = [list(inputs[i]) for i in range(1, N + 1)]

    def is_safe(x, y, direction):
        if direction == 'U' and 0 <= x - 1 < N:
            return grid[x - 1][y] == '0'
        elif direction == 'D' and 0 <= x + 1 < N:
            return grid[x + 1][y] == '0'
        elif direction == 'L' and 0 <= y - 1 < M:
            return grid[x][y - 1] == '0'
        elif direction == 'R' and 0 <= y + 1 < M:
            return grid[x][y + 1] == '0'
        return False

    def escape(x, y):
        if x == 0 or y == 0 or x == N - 1 or y == M - 1:
            return True
        for direction in grid[x][y]:
            if direction == 'S':
                return escape(x - 1, y) or escape(x, y - 1) or escape(x + 1, y) or escape(x, y + 1)
            if is_safe(x, y, direction):
                return escape(x, y - 1) if direction == 'L' else escape(x, y + 1) if direction == 'R' else escape(x - 1, y) if direction == 'U' else escape(x + 1, y)
        return False

    if escape(0, 0):
        return "NOT POSSIBLE"
    else:
        zone_count = 0
        for x in range(N):
            for y in range(M):
                if grid[x][y] == '0' or grid[x][y] == 'S':
                    zone_count += 1
        return zone_count

