    def get_total_expected_guesses(probabilities: list) -> float:
        return sum(get_expected_guesses(probability) for probability in probabilities)

    def get_order(probabilities: list) -> list:
        sorted_probabilities = sorted(enumerate(probabilities), key=lambda x: -x[1])
        return [index for index, _ in sorted_probabilities]

