        total_expected_guesses = get_total_expected_guesses(probabilities)
        order = get_order(probabilities)
        best_guess = 0

        for i in range(1, len(probabilities) + 1):
            current_guess = 0
            for j in range(i):
                current_guess += get_expected_guesses(probabilities[order[j]])
            current_guess /= total_expected_guesses
            best_guess = max(best_guess, current_guess)

        return best_guess

