            if street[0] == start and street[1] == end:
                return street[2]
        return float('inf')

    def dijkstra(start: int, end: int) -> float:
        visited_intersections = [False] * (N + 1)
        distances = [float('inf')] * (N + 1)
        distances[start] = 0

        while True:
            current = min(filter(lambda x: not visited_intersections[x], range(1, N + 1)), key=lambda x: distances[x])
            if visited_intersections[current]:
                break

            visited_intersections[current] = True
            if current == end:
                return distances[current]

            for neighbor in range(1, N + 1):
                if not visited_intersections[neighbor]:
                    distance = distances[current] + calculate_distance(current, neighbor)
                    if distance < distances[neighbor]:
                        distances[neighbor] = distance

        return float('inf')

