    def run_length_encoding(message: str) -> str:
        encoded_message = ""
        prev_char = ""
        run_length = 1

        for char in message:
            if char == prev_char:
                run_length += 1
            else:
                if prev_char:
                    encoded_message += prev_char + str(run_length)
                    run_length = 1
                prev_char = char

        # Append the last character and its run length
        encoded_message += prev_char + str(run_length)

        return encoded_message

    def run_length_decoding(encoded_message: str) -> str:
        decoded_message = ""
        i = 0

        while i < len(encoded_message):
            char = encoded_message[i]
            run_length = int(encoded_message[i + 1])
            decoded_message += char * run_length
            i += 2

        return decoded_message

    command, message = stdin.strip().split(" ")

    if command == "E":
        return run_length_encoding(message)
    elif command == "D":
        return run_length_decoding(message)
    else:
        raise ValueError("Invalid command")

