        type_count = {}
        for event_type in types:
            type_count[event_type] = type_count.get(event_type, 0) + 1

        unique_types = [k for k, v in type_count.items() if v == 1]
        if len(unique_types) < 2:
            return 0

        return len(unique_types) * (len(unique_types) - 1)

    types = [char for char in stdin]
    return count_good_itineraries(types)

