    characters = {}
    for i in range(1, num_chars + 1):
        name, language, *languages = input_data[i].split()
        characters[name] = {'language': language, 'languages': set(languages)}

    def can_converse(char1, char2):
        return char1['language'] != char2['language'] and \
               (char1['language'] in char2['languages'] or char2['language'] in char1['languages'])

    def bfs(start, end):
        visited = set()
        queue = [(start, 0)]
        while queue:
            node, level = queue.pop(0)
            if node == end:
                return level
            visited.add(node)
            for neighbor in nodes[node]:
                if neighbor not in visited:
                    queue.append((neighbor, level + 1))
        return -1

    nodes = {name: [char for char in characters if can_converse(characters[char], characters[name])] for name in characters}
    leaves = [name for name in characters if not nodes[name]]
    size_leaves = len(leaves)
    for i in range(len(leaves), num_chars):
        new_leaves = leaves[:]
        for j in range(len(leaves)):
            for k in range(j + 1, len(leaves)):
                if bfs(leaves[j], leaves[k]) == -1:
                    new_leaves.remove(leaves[j])
                    new_leaves.remove(leaves[k])
                    break
            else:
                continue
            break
        size_leaves = min(size_leaves, len(new_leaves))
        leaves = new_leaves
    return size_leaves

