                    for k in range(max(i-1, 0), min(i+2, N)):
                        for l in range(max(j-1, 0), min(j+2, M)):
                            if k != i or l != j:
                                new_grid[k][l] = "V"

        grid = new_grid

    # Print the resulting grid
    for i in range(N):
        print("".join(grid[i]))

