def solution(stdin: str) -> str:
    n = int(stdin.splitlines()[0])
    stds = list(map(int, stdin.splitlines()[1].split()))
    if n == 2:
        return "impossible"
    total_stds = sum(stds)
    exams = [1] * (total_stds + n - 1)
    for i in range(n):
        stds_to_distribute = sum(exams[:stds[i]])
        if stds_to_distribute < total_stds:
            return "impossible"
        exams_to_distribute = [0] * stds[i]
        for j in range(stds[i]):
            exams_to_distribute[j] = exams.pop(0)
        exams_to_distribute.sort(reverse=True)
        for j in range(stds[i]):
            exams.append(exams_to_distribute[j])
    return " ".join(str(i) for i in range(1, n+1))

