def solution(stdin: str) -> str:
    # If the numbers are divisors, then the number is divisor of both of them
    n = int(stdin.readline())
    divisors = [int(x) for x in stdin.readline().split()]
    if len(divisors)!= n:
        raise ValueError("Invalid number of divisors.")
    for x in divisors:
        if x not in divisors:
            raise ValueError("Divisor must be a divisor of all numbers.")
    divisors.sort()
    x = divisors[0]
    y = divisors[1]
    for i in range(2, n):
        d = divisors[i]
        if d % x == 0 and d % y == 0:
            return str(x) + " " + str(y)
        elif d % x == 0:
            y *= d // x
        else:
            x *= d // y
    return "No solution"

