def solution(stdin: str) -> str:
    n, k = [int(x) for x in stdin.split()]
    s = stdin[:n]

    # Given that we know that there are only K distinct substrings,
    # we can use a trie with a single node to create all the substrings
    # that are of length at most K.
    # To create a trie with a single node, we create a set of the
    # suffixes that have already been visited. We then add the
    # prefix to the trie with the suffix at the end, and clear the suffix.
    # Since the suffix is now the empty string, we add the prefix to the trie.

    visited = set()

    def new_node(suffix: str) -> str:
        if suffix not in visited:
            visited.add(suffix)
            trie[suffix] = []

    def add_suffix(suffix: str) -> None:
        node = trie[suffix]
        if not node:
            new_node(suffix)
        node.append(prefix + suffix)
        node = trie[suffix]
        node.append(prefix + suffix)
        trie[suffix] = []

    for i in range(n - 1, -1, -1):
        if s[i] not in trie:
            trie[s[i]] = []
        add_suffix(s[i:])
        add_suffix(s[i] + s[i + 1:])

    def match_suffix(suffix: str) -> str:
        if len(suffix) > k:
            return ""
        if suffix not in trie:
            return ""
        return trie[suffix][0]

    substrings = []
    for i in range(n - k + 1):
        new_node(s[i:i + k])
        substrings.append(match_suffix(s[i:i + k]))
    return ''.join(substrings)

