    n = int(stdin.readline())
    segments = [map(int, stdin.readline().split()) for i in range(n)]
    if n == 1:
        return 0

    sorted_segments = sorted(segments, key=lambda x: x[1])
    max_i = n - 1
    for i in range(n - 1):
        if sorted_segments[i][0] > sorted_segments[i + 1][1]:
            max_i = i
            break

    if max_i == 0:
        return 0

    intersects = set()
    for i in range(max_i):
        for j in range(i + 1, max_i + 1):
            if sorted_segments[i][1] >= sorted_segments[j][0]:
                intersects.add((sorted_segments[i][0], sorted_segments[j][1]))

    intersects.remove(sorted_segments[max_i])
    if intersects:
        return max(intersects, key=lambda x: x[1] - x[0])[1] - max(intersects, key=lambda x: x[1] - x[0])[0]
    else:
        return 0

