def solution(stdin: str) -> str:
    """https://www.codewars.com/kata/count-cost-of-string-subsequences

    Given a string $s$, find the minimum total cost to get a set of size $k$ or report that it is impossible to do so.
    """
    n, k = map(int, stdin.split(' '))
    s = stdin.strip()
    if n < k:
        return '-1'
    if k == 1:
        return str(0)
    s = ''.join(sorted(s))
    if k >= len(s):
        return str(0)
    return str(sum([x for i, x in enumerate(range(n - k + 1, n + 1)) if s[i]!= s[i - 1]]))

