    def is_symmetric(a, b):
        return a[0] == b[1] and a[1] == b[0]
    def get_neighbors(tiles, i, j):
        n = len(tiles)
        neighbors = [(i - 1, j), (i + 1, j), (i, j - 1), (i, j + 1)]
        neighbors = [(i, j) for i, j in neighbors if 0 <= i < n and 0 <= j < n]
        return neighbors
    def is_square(tiles):
        n = len(tiles)
        for i in range(n):
            for j in range(n):
                neighbors = get_neighbors(tiles, i, j)
                if not is_symmetric(tiles[i], tiles[j]) or not is_symmetric(tiles[neighbors[0][0]], tiles[neighbors[0][1]]) or not is_symmetric(tiles[neighbors[1][0]], tiles[neighbors[1][1]]):
                    return False
        return True
    if not is_square(tiles):
        return "NO"
    return "YES"

