        return "NO"

    a = []
    for _ in range(k):
        a.append(int(stdin.readline().strip()))

    # If the sum of a does not equal n, we can't
    # solve for any array.
    if sum(a)!= n:
        return "NO"

    # If we only have to solve once, we can
    # solve n - 1 problems.
    if k == 1:
        return "YES\n" + " ".join(map(str, a))

    # Otherwise we have to solve k - 1 problems
    # during each day.
    else:
        a.sort()
        # We can't solve more than twice each number
        # if we solve more than once.
        for i in range(k - 1):
            if a[i] > 2 * a[i + 1]:
                return "NO"

        # Since we can't solve more than twice the number
        # when we solve once, we can solve exactly
        # n - 1 problems during each day.
        return "YES\n" + " ".join(map(str, a))

