                tree[j][i].append(j)

    def find_cycle(root: int, cycle: List[int]) -> bool:
        for vertex in tree[root][root]:
            if vertex in cycle:
                return True

            cycle.append(vertex)
            if find_cycle(vertex, cycle):
                return True

        cycle.pop()
        return False

