    def left_most_ravioli_in_stack(stack: list) -> int:
        return stack[0]

    def height_of_stack(stack: list) -> int:
        return len(stack)

    def build_stack(stack: list, a: list) -> list:
        stack.append(a.pop(0))
        return stack

    def sort_stack(stack: list, output: list) -> list:
        while height_of_stack(stack) > 1:
            if left_most_ravioli_in_stack(stack) > left_most_ravioli_in_stack(stack[-1]):
                output.append(height_of_stack(stack[-1]))
                stack.pop()
            else:
                output.append(height_of_stack(stack))
                stack.pop()
        return stack

    def output_array(stack: list, output: list) -> list:
        while height_of_stack(stack) > 1:
            output.append(height_of_stack(stack[-1]))
            stack.pop()
        output.append(height_of_stack(stack))
        return output

    stack = []
    output = []
    while a:
        stack = build_stack(stack, a)
        stack = sort_stack(stack, output)
        stack = build_stack(stack, a)
        stack = sort_stack(stack, output)

    while height_of_stack(stack) > 1:
        output.append(height_of_stack(stack[-1]))
        stack.pop()

    output = output_array(stack, output)
    if height_of_stack(stack) == 1:
        output.append(height_of_stack(stack))

    return 'YES' if sorted(output) == output else 'NO'

