    n = int(stdin.readline())
    s = stdin.readline().strip()
    t = stdin.readline().strip()

    if s == t:
        return 0

    last_swap = {}

    for i in range(n):
        if s[i]!= t[i]:
            last_swap[i] = (s[i], t[i])

    moves = [0]

    def next_moves(i: int, moves: list) -> list:
        if i > n - 2:
            return moves

        next_moves_i = []

        for j in range(i + 1, n):
            if j in last_swap:
                moves.append(last_swap[j][0])
                next_moves_i.append(j)
                next_moves_i.append(j + 1)

        return next_moves(i + 1, moves) + next_moves_i

    moves = next_moves(0, moves)

    if len(moves) > 10**4:
        return -1

    return str(len(moves)) + '\n' +''.join(str(move) for move in moves)

