        stack = []
        for c in s:
            if c == '(':
                stack.append(c)
            else:
                if len(stack) == 0 or stack.pop()!= '(':
                    return False
        return len(stack) == 0

    count = 0
    for i in range(n):
        if not isValid(s):
            continue

        if validBracketSequence(s):
            count += 1

        if isValid(transform(s)):
            count += 1

        s = transform(s)

    return count

