    # 1 <= n <= 10^5
    # n is an even number
    # 1 <= d_i <= 10^5
    # All values in input are integers.
    def diff(a: list, n: int) -> list:
        # calculates the difference between each consecutive values of the list
        b = [a[i] - a[i-1] for i in range(1, n)]
        return b
    d = diff(a, n)
    def arr_arc(n: int, d: list) -> int:
        # counts the number of d if they are all in ARCs and not ABCs
        b = 0
        for i in d:
            if i >= n-1:
                b += 1
        return b
    def abc_abc(n: int, d: list) -> int:
        # counts the number of d if they are all in ABCs and not ARCs
        b = 0
        for i in d:
            if i <= n-1:
                b += 1
        return b
    def k(n: int, d: list) -> int:
        # counts the number of d if they are all in ARCs and not ABCs
        b = 0
        for i in d:
            if i >= n:
                b += 1
        return b
    def arr_abc(n: int, d: list) -> int:
        # counts the number of d if they are all in ABCs and not ARCs
        b = 0
        for i in d:
            if i <= n:
                b += 1
        return b
    d_arc = diff(a, n)
    d_abc = diff(a, n)
    k_arc = k(n, d_arc)
    k_abc = k(n, d_abc)
    arr_arc = arr_arc(n, d_arc)
    abc_abc = abc_abc(n, d_abc)
    arr_abc = arr_abc(n, d_abc)
    if arr_arc == arr_abc and k_arc == k_abc:
        return "2"
    else:
        return str(arr_arc - arr_abc)
