    def int_sieve(n):
        """ Returns a list of all prime numbers upto n"""
        sieve = list(range(n + 1))
        sieve[1] = 0
        for i in range(2, int(math.sqrt(n)) + 1):
            if sieve[i]:
                sieve[i * i: n + 1: i] = [0] * len(sieve[i * i: n + 1: i])
        return [x for x in sieve[2:] if x]

    def perfect_power(n):
        """ Returns 1 if n is a perfect power, otherwise returns 0"""
        n = abs(n)
        l = int_sieve(n)
        for i in l:
            if n == i ** 2:
                return 1
            elif n % i == 0:
                return 0
        return 0

    def closest_power(n):
        """ Returns the closest power of 2, which is smaller than n"""
        n = abs(n)
        for i in range(len(l) - 1, -1, -1):
            if l[i] > n:
                return l[i - 1]
            elif l[i] == n:
                return l[i]

    def all_divisors(n):
        """ Returns a list of all divisors excluding 1 and n"""
        divs = [1]
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                divs.append(i)
                divs.append(n // i)
        return divs

    def is_almost_all(n, d):
        """ Returns 1 if d is a list of all divisors except 1 and n"""
        for i in d:
            if i not in [1, n]:
                return 0
        return 1

    def is_contradictory(n, d):
        """ Returns 1 if d is a list of all divisors except 1 and n"""
        for i in d:
            if i in [1, n]:
                return 1
        return 0

    def min_x(n, d):
        """ Returns the minimum possible x for a given n and d"""
        if perfect_power(n):
            if is_almost_all(n, d):
                return 1
            else:
                return 2 ** closest_power(n)
        elif is_almost_all(n, d):
            return n
        else:
            divs = all_divisors(n)
            for i in divs:
                if is_almost_all(i, d) and is_contradictory(i, d):
                    return i

