def solution(stdin: str) -> str:
    import re
    reg = re.compile("(?<!\w)([a-z])(?!\w)")
    n = int(stdin.strip())
    ans = 'a'
    while n > 0:
        n -= 1
        if n % 26 == 0:
            ans = chr(ord(ans) + 1) + ans
        else:
            ans = reg.sub(lambda m: chr(ord(m.group(1)) + (ord(ans[0]) - ord('a') + 1) % 26), ans)
        n //= 26
    return ans

