def solution(stdin: str) -> str:
    n, k = list(map(int, stdin.split()))
    a = list(map(int, stdin.split()))
    m = len(a)
    dp = [[0 for _ in range(m + 1)] for _ in range(k + 1)]
    for i in range(1, k + 1):
        for j in range(1, m + 1):
            if i >= a[j - 1]:
                dp[i][j] = max(dp[i][j - 1], dp[i - a[j - 1]][j - 1] + a[j - 1])
            else:
                dp[i][j] = dp[i][j - 1]
    return '\n'.join(map(str, dp[k][m]))

