    while high - low > 1:
        guess = (high + low) // 2
        if (guess == 0 and K == 0) or (guess == K and N > K):
            break

        if sum(scores[:guess]) < M:
            low = guess
        elif sum(scores[:guess]) > M:
            high = guess
        else:
            break

    if sum(scores[:guess]) < M:
        return str(guess)
    return str(-1)

