    def is_sorted(seq):
        for i in range(len(seq) - 1):
            if seq[i] > seq[i + 1]:
                return False
        return True
    def get_seq(col):
        return [grid[i][col] for i in range(rows)]
    def is_sorted_row(row):
        return is_sorted(get_seq(0))
    def is_sorted_col(col):
        return is_sorted(get_seq(col))
    def is_sorted_grid():
        return is_sorted([x for i in range(rows) for x in grid[i]])
    def swap(seq, a, b):
        seq[a], seq[b] = seq[b], seq[a]
    def flip_row(row):
        for col in range(cols):
            swap(grid[row], col, cols - 1 - col)
    def flip_col(col):
        for row in range(rows):
            swap(grid[row], row, rows - 1 - row)
    def flip_grid():
        for row in range(rows):
            for col in range(cols):
                swap(grid[row], col, cols - 1 - col)
    def flip_row_step(row, steps):
        if row + steps > rows - 1:
            return
        for i in range(steps):
            flip_row(row)
    def flip_col_step(col, steps):
        if col + steps > cols - 1:
            return
        for i in range(steps):
            flip_col(col)
    def flip_grid_step(steps):
        if steps > rows * cols - 1:
            return
        for row in range(rows):
            for col in range(cols):
                swap(grid[row], col, cols - 1 - col)
    steps = 0
    while not is_sorted_grid():
        for row in operations:
            if row[0] == 'row':
                if int(row[1]) > 0:
                    flip_row_step(int(row[2]), int(row[3]))
                    steps += int(row[3])
                else:
                    flip_row(int(row[2]))
            elif row[0] == 'col':
                if int(row[1]) > 0:
                    flip_col_step(int(row[2]), int(row[3]))
                    steps += int(row[3])
                else:
                    flip_col(int(row[2]))
            elif row[0] == 'grid':
                flip_grid_step(int(row[1]))
                steps += int(row[1])
    if not is_sorted_grid():
        return 'NO'
    r = []
    for row in grid:
        for col in row:
