def solution(stdin: str) -> str:
    n, k, x = [int(x) for x in stdin.strip().split(" ")]
    a = [int(x) for x in stdin.strip().split(" ")]

    def can_repost(a: List[int]) -> bool:
        # sum_of_reposts = sum(a)
        # if sum_of_reposts % k!= 0:
        #     return False

        if len(a) < k:
            return False

        sum_of_reposts = 0
        for i in range(0, len(a) - k + 1):
            if i == 0:
                sum_of_reposts += sum(a[i: i + k])
            else:
                sum_of_reposts += sum(a[i: i + k]) - min(a[i: i + k])
        return sum_of_reposts >= x

    a = sorted(a, reverse=True)

    left = 0
    right = n - 1
    while left <= right:
        mid = left + ((right - left) // 2)
        if can_repost(a[mid:]):
            left = mid + 1
        else:
            right = mid - 1
    if can_repost(a[:left]):
        return sum(a[:left])
    else:
        return -1


