class KruskalMST:
    def __init__(self, graph):
        self.__graph = graph
        self.__vertices = list(graph.get_vertices())
        self.__edges = graph.get_edges()
        self.__costs = []
        self.__edge_idxs = {}

    def get_max_edges(self):
        return len(self.__costs)

    def get_vertices(self):
        return self.__vertices

    def get_edges(self):
        return self.__edges

    def __compute_costs(self):
        # Sort the edges in non-decreasing order
        self.__edges.sort(key=lambda edge: edge[2])

        # Now that the edges are sorted, we will take the
        # first edge and check if it is in the MST.
        # If the edge is in the MST, then we add the cost
        # to the list of costs.
        for edge in self.__edges:
            if self.__is_in_mst(edge[0], edge[1]):
                self.__costs.append(edge[2])

    def __is_in_mst(self, v_1, v_2):
        # For each edge from vertex v_1 to vertex v_2, check
        # if the edge is in the MST
        for edge_idx in self.__edge_idxs[(v_1, v_2)]:
            if edge_idx < len(self.__costs):
                return True

        # If we reach here, the edge is not in the MST
        return False

    def __build_indexes(self):
        for idx, edge in enumerate(self.__edges):
            if edge[0] not in self.__edge_idxs:
                self.__edge_idxs[(edge[0], edge[1])] = []

            self.__edge_idxs[(edge[0], edge[1])].append(idx)

            if edge[1] not in self.__edge_idxs:
                self.__edge_idxs[(edge[1], edge[0])] = []

            self.__edge_idxs[(edge[1], edge[0])].append(idx)

    def get_costs(self):
        if not self.__costs:
            self.__compute_costs()

        return self.__costs

    def get_edge_idx(self, v_1, v_2):
        for edge_idx in self.__edge_idxs[(v_1, v_2)]:
            edge = self.__edges[edge_idx]
            if edge[0] == v_1:
                return edge_idx

        for edge_idx in self.__edge_idxs[(v_1, v_2)]:
            edge = self.__edges[edge_idx]
            if edge[1] == v_1:
                return edge_idx

        raise ValueError("edge doesn't exist.")

class Graph:
    def __init__(self):
        self.__vertices = set()
        self.__edges = []

    def get_vertices(self):
        return self.__vertices

    def get_edges(self):
        return self.__edges

    def build_graph(self, n, edges):
        # Check if we have the correct number of vertices
        if n < 3:
            raise ValueError("Graph must have at least 3 vertices.")

        # Check if there are no self-loops in the graph
        for edge in edges:
            if edge[0] == edge[1]:
                raise ValueError("Graph must not have self-loops.")

        # Check if there is no edge
        for edge in edges:
            if edge[0] > n or edge[1] > n:
                raise ValueError("Graph must not have edges outside the graph.")

        # Add all the vertices to the graph
        for i in range(1, n + 1):
            self.__vertices.add(i)

        # Add all the edges to the graph
        for edge in edges:
            self.__edges.append(edge)
            self.__edges.append((edge[1], edge[0]))

