    N = int(stdin.readline())
    graph = {}
    for _ in range(N - 1):
        a, b = [int(x) for x in stdin.readline().split()]
        graph.setdefault(a, []).append(b)
        graph.setdefault(b, []).append(a)

    # get all nodes
    nodes = set(graph.keys())
    for v in nodes:
        if v in graph:
            for w in graph[v]:
                graph.setdefault(w, []).append(v)

    max_edges = 0
    for s in nodes:
        for t in nodes:
            for v in nodes:
                if v!= s and v!= t and v not in graph[s] and v not in graph[t]:
                    max_edges = max(max_edges, len(graph[s]) + len(graph[t]) - 2)

    # get maximal number of edges
    for s in nodes:
        for t in nodes:
            for v in nodes:
                if v!= s and v!= t and v not in graph[s] and v not in graph[t]:
                    max_edges = max(max_edges, len(graph[s]) + len(graph[t]) - 2)

    # get maximum simple path between nodes
    for s in nodes:
        for t in nodes:
            if s!= t:
                for v in nodes:
                    if v!= s and v!= t and v not in graph[s] and v not in graph[t]:
                        max_edges = max(max_edges, len(graph[s]) + len(graph[t]) - 2)

    return str(max_edges)


