def solution(stdin: str) -> str:
    n = int(stdin.readline().strip())
    arr = list(map(int, stdin.readline().strip().split(' ')))
    memo = dict()
    res = None
    def helper(i, k):
        if (i, k) in memo:
            return memo[i, k]
        elif i >= n:
            return 0
        elif k == 0:
            return max(1, arr[i])
        else:
            return min(helper(i+1, k-1) + max(0, arr[i]), helper(i+1, k) + max(0, arr[i]))
    res = helper(0, n - 1)
    if res is None:
        res = -1
    return res
