def solution(stdin: str) -> str:
    def has_trailing_zero(num: int) -> bool:
        """
        Returns True if there is a zero at the end of the given number, False otherwise.
        """
        return num % 10 == 0 and num // 10!= 0

    n = int(stdin)
    if n < 10:
        return 0
    result = [1]
    num = n
    while True:
        if not num:
            break
        if has_trailing_zero(num):
            num //= 10
            continue
        result.append(1)
        num -= 1
    return len(result)

