        (s, d, c) = stdin[i]
        schedule[s][d] += c
    #print(schedule)
    schedule = schedule[1:]
    #print(schedule)
    for i in range(n):
        if schedule[i][0] > 0:
            return '-1'
        for j in range(1, n+1):
            for k in range(1, j+1):
                if schedule[i][j] == k:
