def solution(stdin: str) -> str:
    n = int(stdin.readline())
    s = stdin.readline().strip()

    # Find all the permutations of colors, and the number of swaps each permutation takes.
    # The swaps take a bit more time but it is done in constant time.
    perms = permutations(s, len(s))
    swaps = [0] * len(s)
    for perm in perms:
        swaps[0] += abs(perm[0] - 1)
        for i in range(1, len(perm)):
            if perm[i]!= perm[i - 1]:
                swaps[i] += 1

    # Find the minimum number of colors.
    res = min(swaps)

    # Color the string using the minimum number of colors.
    c = [0] * n
    for i in range(len(s)):
        c[i] = res
        if i > 0:
            if s[i]!= s[i - 1] or c[i - 1] == res:
                c[i] = c[i - 1] + 1

    return str(res) + '\n' +''.join(str(c[i]) for i in range(n))

