def solution(stdin: str) -> str:
    n, d, k = map(int, stdin.split())

    def is_possible(tree):
        nonlocal n, d, k
        degree = [0] * (n + 1)

        for u, v in tree:
            degree[u] += 1
            degree[v] += 1

        for d in degree:
            if d > k:
                return False

        for u, v in tree:
            if degree[u] > d or degree[v] > d:
                return False

        return True

    def dfs(tree, visited: set, cur: int, level: int):
        nonlocal n, d, k

        if level > d:
            return

        for u, v in tree:
            if u == cur or v == cur:
                continue

            if (u, v) not in visited and (v, u) not in visited:
                visited.add((u, v))
                visited.add((v, u))

                if is_possible(visited):
                    yield level + 1, u, v

                dfs(tree, visited, u, level + 1)
                dfs(tree, visited, v, level + 1)
                visited.remove((u, v))
                visited.remove((v, u))

    tree = []
    for u in range(1, n):
        for v in range(1, n):
            if u == v:
                continue
            tree.append((u, v))

    for level, u, v in dfs(tree, set(), 1, 1):
        print(f'{u} {v}')

