    dp = [[-1 for _ in range(n+1)] for _ in range(n+1)]
    def dfs(i,j,m,n):
        if m == 0 and n == 0:
            return 0
        if m == 0:
            return n+1
        if n == 0:
            return m+1
        if dp[i][j]!= -1:
            return dp[i][j]
