    from functools import lru_cache
    @lru_cache(maxsize=None)
    def count_ways(step_index: int, broken_steps: int) -> int:
        if broken_steps > step_index:
            return 0

        if step_index == 0:
            return 1

        ways = 0
        for i in range(2):
            if broken_steps + i <= step_index:
                ways += count_ways(step_index - i, broken_steps + i)

        return ways

    return str(count_ways(N - 1, 0) % 1000000007)

def solution(stdin: str) -> str:
