    MOD = 10 ** 9 + 7

    def recursive(N: int, M: int) -> int:
        if M == 0:
            return 1
        if N == 1:
            return 1
        if M == 1:
            return 1
        if M == 2 and N == 2:
            return 2
        if M == 3:
            return 4
        return (recursive(N - 1, M - 1) + recursive(N - 2, M - 2)) % MOD
    return str(recursive(N, M))

