class Monster:
    def __init__(self, hp):
        self.hp = hp

    def hp_change(self, d):
        self.d = d
        self.hp_list = [self.hp]
        for i in range(len(d)):
            self.hp_list.append(self.hp + d[i])

    def hp(self):
        return self.hp_list[-1]

    def __add__(self, other):
        return Monster(self.hp + other)

    def __sub__(self, other):
        return Monster(self.hp - other)

