        if i == j == 0:
            return 1
        elif i == 0:
            return f(i, j - 1)
        elif j == 0:
            return f(i - 1, j)
        else:
            return f(i - 1, j) + f(i, j - 1)

    res = 0
    for i in range(n):
        for j in range(m):
            res += f(i, j)

    return res


