        def evaluate(expression: str) -> str:
            """Evaluate the expression and return the result as a string."""
            # Loop over each token.
            for token in expression.strip().split():
                # Parse the token.
                if token.startswith("+"):
                    return evaluate(token[1:]) + evaluate(expression[expression.index(token) + 1:])
                elif token.startswith("-"):
                    return evaluate(token[1:]) - evaluate(expression[expression.index(token) + 1:])
                elif token.isdigit():
                    return ord(token) - ord("0")
            # Nothing to return.
