def solution(stdin: str) -> str:
    n = int(stdin.readline())
    tree = [set() for _ in range(n)]
    for i in range(n - 1):
        a, b = stdin.readline().split()
        tree[int(a) - 1].add(int(b) - 1)
        tree[int(b) - 1].add(int(a) - 1)

    for i in range(n):
        for j in tree[i]:
            tree[j] = tree[i] | tree[j]

    res = 0
    for i in range(n - 1):
        for j in tree[i]:
            res = max(res, len(tree[i]) - len(tree[j]) - 1)

    return str(res)

